mapper.select:

select id, user_id, product_id, quantity, checked, create_time, update_time
from neuedu_cart
where id = #{id,jdbcType=INTEGER}

mapper.delete:
DELETE from neuedu_cart

  user_id = #{userId}

    and product_id in

    #{productId}

mapper.update:
UPDATE  neuedu_cart
SET checked=#{flag}

  user_id=#{userId}

    and product_id=#{productId}
