mapper.update:
update neuedu_shipping
receiver_name = #{shipping.receiverName,jdbcType=VARCHAR}, receiver_phone = #{shipping.receiverPhone,jdbcType=VARCHAR}, receiver_mobile = #{shipping.receiverMobile,jdbcType=VARCHAR}, receiver_province = #{shipping.receiverProvince,jdbcType=VARCHAR}, receiver_city = #{shipping.receiverCity,jdbcType=VARCHAR}, receiver_district = #{shipping.receiverDistrict,jdbcType=VARCHAR}, receiver_address = #{shipping.receiverAddress,jdbcType=VARCHAR}, receiver_zip = #{shipping.receiverZip,jdbcType=VARCHAR}, update_time = now()
where id = #{shipping.id,jdbcType=INTEGER} and user_id=#{userId}

mapper.select:
select id, user_id, receiver_name, receiver_phone, receiver_mobile, receiver_province,
receiver_city, receiver_district, receiver_address, receiver_zip, create_time, update_time
from neuedu_shipping
where user_id = #{userId}
limit #{start},#{size}

select count(1)
from neuedu_shipping
where user_id = #{userId}
